INSERT INTO chatbots (name, primary_color, text_color, background_color, chat_icon, position, welcome_message, navigation_message)
VALUES 
  ('چت‌بات پشتیبانی', '#3B82F6', '#FFFFFF', '#F3F4F6', '💬', 'bottom-right', 'سلام! به سیستم پشتیبانی ما خوش آمدید. چطور می‌توانم کمکتان کنم؟', 'لطفاً سوال خود را بپرسید یا از گزینه‌های زیر انتخاب کنید.')
INSERT INTO chatbot_messages (chatbot_id, user_message, bot_response, user_ip, timestamp)
VALUES
  (1, 'سلام', 'سلام! چطور می‌توانم به شما کمک کنم؟', '192.168.1.1', NOW() - INTERVAL '1 hour'),
  (1, 'محصولات شما چه قیمتی دارند؟', 'قیمت محصولات ما متفاوت است. می‌توانید با مراجعه به صفحه محصولات در وبسایت ما، لیست کامل قیمت‌ها را مشاهده کنید. آیا محصول خاصی مد نظر دارید؟', '192.168.1.2', NOW() - INTERVAL '2 hours'),
  (1, 'آیا ارسال رایگان دارید؟', 'بله، برای سفارش‌های بالای 500 هزار تومان، ارسال رایگان داریم. برای سفارش‌های کمتر، هزینه ارسال بسته به مقصد متفاوت است.', '192.168.1.3', NOW() - INTERVAL '3 hours'),
  (1, 'چطور می‌توانم سفارشم را پیگیری کنم؟', 'شما می‌توانید با وارد کردن کد پیگیری که در ایمیل تأییدیه سفارش برای شما ارسال شده، در بخش "پیگیری سفارش" در وبسایت ما، وضعیت سفارش خود را مشاهده کنید.', '192.168.1.4', NOW() - INTERVAL '4 hours'),
  (1, 'مدت زمان تحویل چقدر است؟', 'معمولاً سفارش‌ها در تهران 1 تا 2 روز کاری و در شهرستان‌ها 2 تا 5 روز کاری زمان می‌برد. البته این زمان بسته به موجودی کالا و مقصد ممکن است متفاوت باشد.', '192.168.1.5', NOW() - INTERVAL '5 hours'),
  (1, 'آیا امکان مرجوع کردن کالا وجود دارد؟', 'بله، شما می‌توانید تا 7 روز پس از دریافت کالا، در صورت عدم استفاده و سالم بودن بسته‌بندی، کالا را مرجوع کنید. برای این کار باید با پشتیبانی ما تماس بگیرید.', '192.168.1.6', NOW() - INTERVAL '6 hours'),
  (1, 'روش‌های پرداخت شما چیست؟', 'ما پرداخت آنلاین (کارت به کارت، درگاه بانکی)، پرداخت در محل، و پرداخت اقساطی را پشتیبانی می‌کنیم. در هنگام ثبت سفارش می‌توانید روش پرداخت دلخواه خود را انتخاب کنید.', '192.168.1.7', NOW() - INTERVAL '7 hours'),
  (1, 'آیا شعبه حضوری دارید؟', 'بله، ما دارای شعبه مرکزی در تهران، خیابان ولیعصر، بالاتر از میدان ونک هستیم. ساعات کاری ما از شنبه تا چهارشنبه 9 صبح تا 6 عصر و پنجشنبه‌ها 9 صبح تا 1 بعدازظهر است.', '192.168.1.8', NOW() - INTERVAL '8 hours'),
  (1, 'گارانتی محصولات چگونه است؟', 'تمامی محصولات ما دارای گارانتی اصالت و سلامت کالا هستند. بسته به نوع محصول، گارانتی‌های متفاوتی از 6 ماه تا 24 ماه ارائه می‌شود که جزئیات آن در صفحه هر محصول ذکر شده است.', '192.168.1.9', NOW() - INTERVAL '9 hours'),
  (1, 'آیا امکان خرید عمده وجود دارد؟', 'بله، برای خرید عمده می‌توانید با بخش فروش سازمانی ما از طریق شماره 021-12345678 تماس بگیرید یا درخواست خود را به ایمیل sales@example.com ارسال کنید تا کارشناسان ما با شما تماس بگیرند.', '192.168.1.10', NOW() - INTERVAL '10 hours'),
  (1, 'محصولات شما گارانتی دارند؟', 'بله، تمام محصولات ما دارای گارانتی اصالت و سلامت هستند. بسته به نوع محصول، مدت گارانتی متفاوت است که در صفحه هر محصول ذکر شده است.', '192.168.1.11', NOW() - INTERVAL '1 day'),
  (1, 'چطور می‌توانم با پشتیبانی تماس بگیرم؟', 'شما می‌توانید از طریق شماره تلفن 021-12345678 در ساعات کاری (شنبه تا پنجشنبه 9 صبح تا 5 عصر)، ایمیل support@example.com، یا چت آنلاین در وبسایت با ما در تماس باشید.', '192.168.1.12', NOW() - INTERVAL '2 days'),
  (1, 'آیا امکان سفارش تلفنی وجود دارد؟', 'بله، شما می‌توانید با شماره 021-87654321 در ساعات کاری تماس بگیرید و سفارش خود را به صورت تلفنی ثبت کنید.', '192.168.1.13', NOW() - INTERVAL '3 days'),
  (1, 'آیا به خارج از کشور هم ارسال دارید؟', 'در حال حاضر، ما فقط در داخل ایران سرویس ارسال داریم و متأسفانه امکان ارسال به خارج از کشور وجود ندارد.', '192.168.1.14', NOW() - INTERVAL '4 days'),
  (1, 'چطور می‌توانم عضو خبرنامه شوم؟', 'شما می‌توانید در فوتر وبسایت ما، ایمیل خود را وارد کرده و روی دکمه "عضویت در خبرنامه" کلیک کنید تا از آخرین محصولات، اخبار و تخفیف‌های ما مطلع شوید.', '192.168.1.15', NOW() - INTERVAL '5 days');
INSERT INTO tickets (chatbot_id, user_ip, subject, message, status, priority, created_at)
VALUES
  (1, '192.168.1.100', 'مشکل در پرداخت', 'من نمی‌توانم پرداخت آنلاین انجام دهم. هر بار خطای "تراکنش ناموفق" دریافت می‌کنم.', 'open', 'high', NOW() - INTERVAL '2 hours'),
  (1, '192.168.1.101', 'تأخیر در ارسال', 'سفارش من با کد پیگیری #12345 بیش از یک هفته است که ارسال نشده است.', 'in_progress', 'medium', NOW() - INTERVAL '1 day'),
  (1, '192.168.1.102', 'کالای اشتباه', 'من گوشی سامسونگ مدل A52 سفارش داده بودم اما مدل A32 دریافت کردم.', 'open', 'high', NOW() - INTERVAL '3 days'),
  (1, '192.168.1.103', 'درخواست مرجوعی', 'می‌خواهم کالایی که خریداری کرده‌ام را مرجوع کنم. شماره سفارش: #67890', 'closed', 'low', NOW() - INTERVAL '5 days'),
  (1, '192.168.1.104', 'سوال درباره گارانتی', 'آیا گارانتی محصولات شامل آسیب‌های فیزیکی هم می‌شود؟', 'resolved', 'low', NOW() - INTERVAL '7 days'),
  (1, '192.168.1.105', 'پیشنهاد همکاری', 'من مدیر یک فروشگاه هستم و می‌خواهم درباره امکان همکاری با شما صحبت کنم.', 'open', 'medium', NOW() - INTERVAL '10 days');
INSERT INTO ticket_responses (ticket_id, message, is_admin, created_at)
VALUES
  (2, 'سلام، من مشکل شما را بررسی کردم. سفارش شما در انبار است و فردا ارسال خواهد شد. از صبر شما متشکرم.', true, NOW() - INTERVAL '12 hours'),
  (4, 'درخواست مرجوعی شما ثبت شد. لطفاً کالا را به آدرس پستی ما ارسال کنید و کد پیگیری را برای ما ارسال نمایید.', true, NOW() - INTERVAL '4 days'),
  (4, 'کالا را ارسال کردم. کد پیگیری پست: 123456789', false, NOW() - INTERVAL '3 days'),
  (4, 'کالای شما دریافت شد و مبلغ به حساب شما بازگردانده خواهد شد. با تشکر', true, NOW() - INTERVAL '2 days'),
  (5, 'گارانتی محصولات ما شامل نقص فنی و مشکلات ناشی از تولید است. آسیب‌های فیزیکی مانند شکستگی یا ضربه شامل گارانتی نمی‌شود.', true, NOW() - INTERVAL '6 days');
