INSERT INTO chatbot_messages (chatbot_id, user_message, bot_response, timestamp, user_ip, user_agent)
VALUES 
(14, 'سلام، چطور می‌توانم محصولات شما را خریداری کنم؟', 'سلام! شما می‌توانید از طریق فروشگاه آنلاین ما به آدرس shop.example.com محصولات ما را مشاهده و خریداری کنید.', NOW() - INTERVAL '1 hour', '192.168.1.1', 'Mozilla/5.0'),
(14, 'آیا ارسال رایگان دارید؟', 'بله، برای خریدهای بالای 500 هزار تومان ارسال رایگان داریم. برای سفارش‌های کمتر، هزینه ارسال بر اساس وزن و مسافت محاسبه می‌شود.', NOW() - INTERVAL '2 hour', '192.168.1.2', 'Mozilla/5.0'),
(14, 'زمان تحویل سفارش چقدر است؟', 'معمولاً سفارش‌ها در تهران بین 1 تا 2 روز کاری و در شهرستان‌ها بین 2 تا 5 روز کاری تحویل داده می‌شوند.', NOW() - INTERVAL '3 hour', '192.168.1.3', 'Mozilla/5.0'),
(14, 'آیا امکان پرداخت در محل وجود دارد؟', 'بله، در تهران و برخی کلان‌شهرها امکان پرداخت در محل فراهم است. در مرحله پرداخت می‌توانید این گزینه را انتخاب کنید.', NOW() - INTERVAL '4 hour', '192.168.1.4', 'Mozilla/5.0'),
(14, 'شرایط گارانتی محصولات چیست؟', 'تمامی محصولات ما دارای گارانتی اصالت و سلامت فیزیکی هستند. برخی محصولات نیز گارانتی 18 ماهه دارند که جزئیات آن در صفحه محصول ذکر شده است.', NOW() - INTERVAL '5 hour', '192.168.1.5', 'Mozilla/5.0'),
(14, 'چگونه می‌توانم سفارشم را پیگیری کنم؟', 'شما می‌توانید با وارد کردن کد پیگیری سفارش در بخش "پیگیری سفارش" در سایت، از وضعیت سفارش خود مطلع شوید. همچنین پیامک‌های مراحل ارسال برای شما ارسال می‌شود.', NOW() - INTERVAL '6 hour', '192.168.1.6', 'Mozilla/5.0'),
(14, 'آیا امکان مرجوع کردن کالا وجود دارد؟', 'بله، شما می‌توانید تا 7 روز پس از دریافت کالا، در صورت عدم استفاده و سالم بودن بسته‌بندی، کالا را مرجوع کنید. برای این کار با پشتیبانی تماس بگیرید.', NOW() - INTERVAL '1 day', '192.168.1.7', 'Mozilla/5.0'),
(14, 'آیا محصولات شما اصل هستند؟', 'بله، تمامی محصولات ما اصل و دارای گارانتی اصالت هستند. ما هیچ محصول تقلبی یا فیک نمی‌فروشیم و اصالت تمام کالاها تضمین شده است.', NOW() - INTERVAL '2 day', '192.168.1.8', 'Mozilla/5.0'),
(14, 'روش‌های پرداخت شما چیست؟', 'ما پرداخت آنلاین با تمام کارت‌های بانکی، پرداخت در محل (در برخی شهرها)، و پرداخت اقساطی (برای خریدهای بالای 5 میلیون تومان) را پشتیبانی می‌کنیم.', NOW() - INTERVAL '3 day', '192.168.1.9', 'Mozilla/5.0'),
(14, 'آیا کد تخفیف دارید؟', 'بله، ما به مناسبت‌های مختلف کدهای تخفیف ارائه می‌دهیم. برای اطلاع از آخرین کدهای تخفیف، می‌توانید در خبرنامه ما عضو شوید یا صفحات اجتماعی ما را دنبال کنید.', NOW() - INTERVAL '4 day', '192.168.1.10', 'Mozilla/5.0'),
(14, 'چگونه می‌توانم با پشتیبانی تماس بگیرم؟', 'شما می‌توانید از طریق شماره 021-12345678 در ساعات کاری (شنبه تا چهارشنبه 9 تا 18 و پنجشنبه 9 تا 13) با پشتیبانی تماس بگیرید. همچنین می‌توانید از طریق فرم تماس در سایت یا ایمیل support@example.com با ما در ارتباط باشید.', NOW() - INTERVAL '5 day', '192.168.1.11', 'Mozilla/5.0'),
(14, 'آیا امکان خرید عمده وجود دارد؟', 'بله، برای خریدهای عمده و سازمانی تخفیف‌های ویژه‌ای در نظر گرفته شده است. لطفاً با بخش فروش سازمانی ما به شماره 021-87654321 تماس بگیرید.', NOW() - INTERVAL '6 day', '192.168.1.12', 'Mozilla/5.0'),
(14, 'آیا به خارج از کشور هم ارسال دارید؟', 'بله، ما به بیشتر کشورها ارسال بین‌المللی داریم. هزینه و زمان ارسال بسته به مقصد متفاوت است. برای اطلاعات بیشتر با پشتیبانی تماس بگیرید.', NOW() - INTERVAL '7 day', '192.168.1.13', 'Mozilla/5.0'),
(14, 'آیا نمایندگی فروش حضوری هم دارید؟', 'بله، ما در تهران، مشهد، اصفهان، شیراز و تبریز نمایندگی فروش حضوری داریم. آدرس دقیق و ساعات کاری هر شعبه در بخش "درباره ما" سایت موجود است.', NOW() - INTERVAL '8 day', '192.168.1.14', 'Mozilla/5.0'),
(14, 'آیا امکان دریافت فاکتور رسمی وجود دارد؟', 'بله، برای دریافت فاکتور رسمی کافیست در هنگام ثبت سفارش، گزینه "نیاز به فاکتور رسمی دارم" را انتخاب کنید و اطلاعات شرکت یا سازمان خود را وارد نمایید.', NOW() - INTERVAL '9 day', '192.168.1.15', 'Mozilla/5.0');
INSERT INTO chatbot_messages (chatbot_id, user_message, bot_response, timestamp, user_ip, user_agent)
VALUES 
(14, 'سلام، امروز چه تخفیف‌هایی دارید؟', 'سلام! امروز تخفیف ویژه 30% روی محصولات الکترونیکی و 20% روی لوازم خانگی داریم. می‌توانید از سایت ما دیدن کنید.', NOW() - INTERVAL '30 minute', '192.168.1.20', 'Mozilla/5.0'),
(14, 'آیا محصول X موجود است؟', 'بله، محصول X در انبار موجود است و آماده ارسال. می‌توانید همین الان سفارش دهید.', NOW() - INTERVAL '45 minute', '192.168.1.21', 'Mozilla/5.0'),
(14, 'هزینه ارسال به شهرستان چقدر است؟', 'هزینه ارسال به شهرستان بین 20 تا 50 هزار تومان متغیر است و بستگی به وزن بسته و مسافت دارد. در زمان نهایی کردن سبد خرید، هزینه دقیق محاسبه می‌شود.', NOW() - INTERVAL '1 hour', '192.168.1.22', 'Mozilla/5.0'),
(14, 'آیا امکان ارسال در روز تعطیل وجود دارد؟', 'خیر، ارسال سفارشات فقط در روزهای کاری انجام می‌شود. سفارشات ثبت شده در روزهای تعطیل، در اولین روز کاری بعدی پردازش و ارسال می‌شوند.', NOW() - INTERVAL '2 hour', '192.168.1.23', 'Mozilla/5.0'),
(14, 'چگونه می‌توانم عضو باشگاه مشتریان شوم؟', 'برای عضویت در باشگاه مشتریان، کافیست در سایت ثبت‌نام کنید و پس از اولین خرید، به صورت خودکار عضو باشگاه مشتریان می‌شوید و از امتیازات و تخفیف‌های ویژه بهره‌مند خواهید شد.', NOW() - INTERVAL '3 hour', '192.168.1.24', 'Mozilla/5.0');
